            window.location = "/";
        } catch (err) {
            console.error(err.message);
        }
    }

  return (
    <Fragment>
      <button
        type="button"
        class="btn btn-warning"
        data-toggle="modal"
        data-target={`#id${material.id}`}
      >
        Edit
      </button>
