                setDescription(e.target.value) } />
            </div>

            <div class="modal-footer">
            <button 
                type="button" 
                class="btn btn-warning" 
                data-dismiss="modal"
                onClick = {e => updateDescription(e)}
                >
                Edit
              </button>
              <button 
                type="button" 
                class="btn btn-danger" 
                data-dismiss="modal" 
                onClick ={() => setDescription(material.description)}>
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
