        try{
            const response = await fetch("http://localhost:5000/material");
            const jsonData = await response.json();

            setTodos(jsonData);

        }
        catch(err){
            console.error(err.message);
        }
    };

    useEffect(() => {
        getTodos();
    }, []);
    console.log(todos);
  return (
    <Fragment>
      <table class="table mt-5 text-center">
        <thead>
          <tr>
            <th>Name</th>
            <th>Volume</th>
            <th>Delivery Date</th>
            <th>Color</th>
            <th>Cost</th>
          </tr>
        </thead>
        <tbody>
            {/*<tr>
            <td>John</td>
            <td>Doe</td>
            <td>john@example.com</td>
          </tr> */}
          {materials.map(material => (
              <tr key = {material.id}>
