import React, { Fragment, useEffect, useState } from "react";

import EditMaterial from "./EditMaterial";
import { MaterialRow } from "./MaterialRow";

const ListMaterials = () => {
  const [materials, setMaterials] = useState([]);

  //delte todo function

  const deleteMaterial = async (id) => {
    try {
      const deleteMaterial = await fetch(
        `http://localhost:5000/material/${id}`,
        {
          method: "DELETE",
        }
      );
      setMaterials(materials.filter((material) => material.id !== id));
    } catch (err) {
      console.error(err.message);
    }
  };
  const getMaterials = async () => {
    try {
      const response = await fetch("http://localhost:5000/material");
      const jsonData = await response.json();

      setMaterials(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getMaterials();
  }, []);
  console.log(materials);
  return (
    <Fragment>
      {/* {materials.map((material) => (

          <MaterialRow material={material}></MaterialRow> 

      ))} */}

      <table class="table mt-5 text-center">
        <thead>
          <th>Materials</th>
          <tr>
          <th>Name</th>
          <th>Delete</th>

            {/* <th>Volume</th>
            <th>Delivery Date</th>
            <th>Color</th>
            <th>Cost</th> */}
          </tr>
        </thead>
        <tbody>
            {materials.map((material) => (
              <tr key = {material.id}>
              <td><MaterialRow material={material}></MaterialRow></td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => deleteMaterial(material.id)}
                >
                  Delete
                </button>
              </td>
              </tr>
            ))}

          {/* {materials.map((material) => (
            <tr key={material.id}>
              <td>{material.name}</td>
              <td>{material.volume}</td>
              <td>{material.delivery_date}</td>
              <td>{material.color}</td>
              <td>{material.cost}</td>
              <td>
                <EditMaterial material={material} />
              </td>
              <td>
                <button
                  className="btn btn-danger"
                  onClick={() => deleteMaterial(material.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))} */}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ListMaterials;
